css:
.employee-slip {
    page-break-after: auto;
}

table {
    width: 100%;
    border-collapse: collapse;
}

.employee-code,
.employee-code-h {
    width: 15%;
    font-weight: bold;
}

.employee-name,
.employee-name-h {
    width: 20%;
    font-weight: bold;
}

.date,
.date-h {
    width: 10%;
    font-weight: bold;
}

.weekday,
.weekday-h {
    width: 10%;
    font-weight: bold;
}

.in-time,
.in-time-h,
.out-time,
.out-time-h {
    width: 10%;
    font-weight: bold;
}

.final-salary-breakup {
    margin-top: 20px;
    font-size: 9px;
}

.table-headings {
    font-size: 12px;
    font-weight: bold;
}

.net-salary {
    border: 2px solid;
}

th,
td {
    border: 1px solid black;
    padding: 8px;
    text-align: left;
    font-weight: bold;
}

th {
    width: 10%;
}

td {
    width: 10%;
    font-size: 9px;
}
